import './App.css';
import MenuItem from './components/MenuItem';
import { useState } from 'react';

// import 'bootstrap/dist/css/bootstrap.min.css'; // This imports bootstrap css styles. You can use bootstrap or your own classes by using the className attribute in your elements.

// Menu data. An array of objects where each object represents a menu item. Each menu item has an id, title, description, image name, and price.
// You can use the image name to get the image from the images folder.
const menuItems = [
  {
    id: 1,
    title: 'Gyoza',
    description: 'Japanese dumplings',
    imageName: 'gyoza.png',
    price: 5.99,
  },
  {
    id: 2,
    title: 'Sushi',
    description: 'Japanese rice rolls',
    imageName: 'sushi.png',
    price: 6.99,
  },
  {
    id: 3,
    title: 'Ramen',
    description: 'Japanese noodle soup',
    imageName: 'ramen.png',
    price: 7.99,
  },
  {
    id: 4,
    title: 'Matcha Cake',
    description: 'Japanese green tea cake',
    imageName: 'matcha-cake.png',
    price: 4.99,
  },
  {
    id: 5,
    title: 'Mochi',
    description: 'Japanese rice cake',
    imageName: 'mochi.png',
    price: 3.99,
  },
  {
    id: 6,
    title: 'Yakitori',
    description: 'Japanese skewered chicken',
    imageName: 'yakitori.png',
    price: 2.99,
  },
  {
    id: 7,
    title: 'Takoyaki',
    description: 'Japanese octopus balls',
    imageName: 'takoyaki.png',
    price: 5.99,
  },
  {
    id: 8,
    title: 'Sashimi',
    description: 'Japanese raw fish',
    imageName: 'sashimi.png',
    price: 8.99,
  },
  {
    id: 9,
    title: 'Okonomiyaki',
    description: 'Japanese savory pancake',
    imageName: 'okonomiyaki.png',
    price: 6.99,
  },
  {
    id: 10,
    title: 'Katsu Curry',
    description: 'Japanese curry with fried pork',
    imageName: 'katsu-curry.png',
    price: 9.99,
  }
];

function App() {
  const [totalPrice, setTotalPrice] = useState(0);
  const [quantities, setQuantities] = useState({}); // Track quantities for each menu item

  // Update total price and quantities
  const updateTotalPrice = (price, quantity, title, newCount) => {
    const updatedPrice = price * quantity + totalPrice;
    setTotalPrice(parseFloat(updatedPrice.toFixed(2)));

    const updatedQuantities = { ...quantities, [title]: newCount };
    setQuantities(updatedQuantities);
  };

  // Reset quantities and total price
  const clearAll = () => {
    setTotalPrice(0);
    setQuantities({});
  };

  return (
    <div className="container">
      <img className="logo" src={`${process.env.PUBLIC_URL}/images/logo.png`} alt="logo" />
      <div className="description-text">
        <h1 className="subtitle-1">Delicious, From-Scratch Recipes Close at Hand</h1>
        <h1 className="subtitle-2">The Fresh Choice of UT!</h1>
      </div>

      <div className="menu-items-container">
        {menuItems.map((item) => (
          <MenuItem
            key={item.id}
            title={item.title}
            description={item.description}
            imageName={item.imageName}
            price={item.price}
            quantity={quantities[item.title] || 0}  // Pass quantity as prop
            setCount={(newCount) => updateTotalPrice(item.price, quantities[item.title] || 0, item.title, newCount)} // Pass setCount function as prop
          />
        ))}
      </div>

      <div className="bottom-bar">
        <p>Subtotal: ${totalPrice}</p>
        <button className="btn-primary">Order</button>
        <button className="btn-primary" onClick={clearAll}>Clear All</button>
      </div>
    </div>
  );
}

export default App;
