import React from 'react';

const MenuItem = ({ title, description, imageName, price, quantity, setCount }) => {
    return (
        <div className="menu-item">
            <img src={`${process.env.PUBLIC_URL}/images/${imageName}`} alt={title} />
            <h2>{title}</h2>
            <p>{description}</p>
            <p>${price}</p>
            <div className="quantity-controls">
                <button onClick={() => setCount(quantity - 1)}>-</button>
                <span>{quantity}</span> {/* Display current quantity */}
                <button onClick={() => setCount(quantity + 1)}>+</button>
            </div>
        </div>
    );
};

export default MenuItem;
